import React, { Component } from "react";
import {
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
} from "@material-ui/core";
import "./FeedbackForm.css";

class FeedbackForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      choices: [], // Initialize choices as an array
    };
  }

  componentDidMount() {
    fetch(
      "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
    )
      .then((response) => response.json())
      .then((data) => {
        if (data && data.feedbackQuestions && data.choices) {
          const choices = data.feedbackQuestions.map(() => ""); // Initialize choices with empty strings
          this.setState({
            questions: data.feedbackQuestions,
            choices: choices,
          });
        } else {
          console.error(
            "Error fetching data: Questions or choices data not found"
          );
        }
      })
      .catch((error) => console.error("Error fetching data:", error));
  }

  handleChange = (index, choice) => {
    this.setState((prevState) => {
      const updatedChoices = [...prevState.choices];
      updatedChoices[index] = choice;
      return { choices: updatedChoices };
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const { questions, choices } = this.state;

    if (choices.some((choice) => choice === "")) {
      alert("Please answer all questions before submitting.");
      return;
    } else {
      alert("Form submitted successfully");
    }

    const feedback = { feedback: { questions, choices } };
    console.log(feedback);
  };

  render() {
    const { questions, choices } = this.state;

    return (
      <div className="container">
        <h1>Feedback form</h1>
        <form onSubmit={this.handleSubmit}>
          {questions.map((question, index) => (
            <div className="question-container" key={index}>
              <p className="question-text">{question}</p>
              <div className="radio-options">
                <FormControl component="fieldset">
                  <RadioGroup
                    value={choices[index]}
                    onChange={(e) => this.handleChange(index, e.target.value)}
                  >
                    {Array.isArray(data.choices[index]) &&
                      data.choices[index].map((choice, choiceIndex) => (
                        <FormControlLabel
                          key={choiceIndex}
                          value={choice}
                          control={<Radio />}
                          label={choice}
                        />
                      ))}
                  </RadioGroup>
                </FormControl>
              </div>
            </div>
          ))}
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </form>
      </div>
    );
  }
}

export default FeedbackForm;
