import React, { Component } from "react";
import {
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
} from "@material-ui/core";
import "./FeedbackForm.css";

class FeedbackForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      choices: [],
      loading: true, // Add loading state to manage API call loading
    };
  }

  componentDidMount() {
    fetch(
      "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
    )
      .then((response) => response.json())
      .then((data) => {
        if (data && data.feedbackQuestions && data.choices) {
          // Process data and set state
          this.setState({
            questions: data.feedbackQuestions,
            choices: data.choices.map(() => ""), // Initialize choices array with empty strings
            loading: false, // Set loading to false after data is fetched
          });
        } else {
          console.error(
            "Error fetching data: Questions or choices data not found"
          );
          this.setState({ loading: false }); // Set loading to false even if there's an error
        }
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        this.setState({ loading: false }); // Set loading to false in case of error
      });
  }

  handleChange = (index, choice) => {
    const choices = [...this.state.choices];
    choices[index] = choice;
    this.setState({ choices });
  };

  handleSubmit = (e) => {
    e.preventDefault(); // Prevent form submission

    const { questions, choices } = this.state;

    // Check if any question has not been answered
    if (choices.length !== questions.length || choices.includes("")) {
      alert("Please answer all questions before submitting.");
      return; // Exit early if any question is not answered
    } else {
      alert("Form submitted successfully");
    }

    // Construct feedback object
    const feedback = { feedback: { questions, choices } };

    // Perform submission logic
    console.log(feedback); // You can replace this with actual submission logic
  };

  render() {
    const { questions, choices, loading } = this.state;

    if (loading) {
      return <div>Loading...</div>; // Show loading message while data is being fetched
    }

    return (
      <div className="container">
        <h1>Feedback form</h1>
        <form onSubmit={this.handleSubmit}>
          {questions.map((question, index) => (
            <div className="question-container" key={index}>
              <p className="question-text">{question}</p>
              <div className="radio-options">
                <FormControl component="fieldset">
                  <RadioGroup
                    value={choices[index]}
                    onChange={(e) => this.handleChange(index, e.target.value)}
                  >
                    {/* Render each choice as a radio button */}
                    {choices[index].map((choice, choiceIndex) => (
                      <FormControlLabel
                        key={choiceIndex}
                        value={choice}
                        control={<Radio checked={choices[index] === choice} />} // Mark the radio button as checked if the choice matches the selected value
                        label={choice}
                      />
                    ))}
                  </RadioGroup>
                </FormControl>
              </div>
            </div>
          ))}
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </form>
      </div>
    );
  }
}

export default FeedbackForm;
