import React, { Component } from "react";
import {
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Button,
} from "@material-ui/core";

class FeedbackForm extends Component {
  state = {
    questions: [],
    choices: [],
    feedback: {
      questions: [],
      choices: [],
    },
    companyLogo: "",
    unitName: "",
  };

  componentDidMount() {
    this.fetchQuestions();
  }

  fetchQuestions = () => {
    fetch(
      "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
    )
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        if (data.status === 1) {
          const questions = data.feedbackQuestions.map((question, index) => ({
            question: question,
            choices: data.choices[index],
            answer: "",
          }));
          this.setState({
            questions,
            companyLogo: data.companyLogo,
            unitName: data.unitName,
          });
        } else {
          throw new Error(data.error_msg || "Error fetching questions");
        }
      })
      .catch((error) =>
        console.error("There was an error fetching the questions:", error)
      );
  };

  handleChoiceChange = (index, choice) => {
    const questions = [...this.state.questions];
    questions[index].answer = choice;
    this.setState({ questions });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const feedback = {
      questions: this.state.questions.map((q) => q.question),
      choices: this.state.questions.map((q) => q.answer),
    };
    console.log("Feedback submitted:", feedback);
    // Here you can also post the feedback to a backend endpoint if required
  };

  renderQuestion = (question, index) => (
    <FormControl component="fieldset" key={index}>
      <FormLabel component="legend">{question.question}</FormLabel>
      <RadioGroup
        aria-label={question.question}
        name={question.question}
        value={question.answer}
        onChange={(event) => this.handleChoiceChange(index, event.target.value)}
      >
        {question.choices.map((choice, choiceIndex) => (
          <FormControlLabel
            key={choiceIndex}
            value={choice}
            control={<Radio />}
            label={choice}
          />
        ))}
      </RadioGroup>
    </FormControl>
  );

  render() {
    return (
      <div>
        <img src={this.state.companyLogo} alt="Company Logo" />
        <h2>Feedback for {this.state.unitName}</h2>
        <form onSubmit={this.handleSubmit}>
          {this.state.questions.map(this.renderQuestion)}
          <Button type="submit" variant="contained" color="primary">
            Submit Feedback
          </Button>
        </form>
      </div>
    );
  }
}

export default FeedbackForm;
