import React, { Component } from "react";
import {
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
} from "@material-ui/core";

class FeedbackForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      choices: [],
    };
  }

  componentDidMount() {
    fetch(
      "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
    )
      .then((response) => response.json())
      .then((data) => {
        // Process data and set state
        this.setState({
          questions: data.questions,
          choices: Array(data.questions.length).fill(""),
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }

  handleChange = (index, choice) => {
    const choices = [...this.state.choices];
    choices[index] = choice;
    this.setState({ choices });
  };

  handleSubmit = () => {
    const { questions, choices } = this.state;
    const feedback = { feedback: { questions, choices } };
    console.log(feedback); // You can replace this with actual submission logic
  };

  render() {
    const { questions, choices } = this.state;

    return (
      <form onSubmit={this.handleSubmit}>
        {questions.map((question, index) => (
          <FormControl component="fieldset" key={index}>
            <RadioGroup
              value={choices[index]}
              onChange={(e) => this.handleChange(index, e.target.value)}
            >
              <legend>{question}</legend>
              <FormControlLabel
                value="Excellent"
                control={<Radio />}
                label="Excellent"
              />
              <FormControlLabel value="Good" control={<Radio />} label="Good" />
              <FormControlLabel value="Fair" control={<Radio />} label="Fair" />
              <FormControlLabel value="Poor" control={<Radio />} label="Poor" />
            </RadioGroup>
          </FormControl>
        ))}
        <Button type="submit" variant="contained" color="primary">
          Submit
        </Button>
      </form>
    );
  }
}

export default FeedbackForm;
