// FeedbackForm.js

import React, { Component } from "react";
import {
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
} from "@material-ui/core";
import "./FeedbackForm.css";

class FeedbackForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      choices: [],
    };
  }

  componentDidMount() {
    fetch(
      "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
    )
      .then((response) => response.json())
      .then((data) => {
        if (data && data.feedbackQuestions) {
          // Process data and set state
          this.setState({
            questions: data.feedbackQuestions,
            choices: data.choices.map((choiceArray) => choiceArray[0]), // Assuming you want to use the first choice in each array
          });
        } else {
          console.error("Error fetching data: Questions data not found");
        }
      })
      .catch((error) => console.error("Error fetching data:", error));
  }

  handleChange = (index, choice) => {
    const choices = [...this.state.choices];
    choices[index] = choice;
    this.setState({ choices });
  };

  handleSubmit = () => {
    const { questions, choices } = this.state;
    const feedback = { feedback: { questions, choices } };
    console.log(feedback); // You can replace this with actual submission logic
  };

  render() {
    const { questions, choices } = this.state;

    return (
      <div className="container">
        {" "}
        {/* Apply the container class here */}
        <form onSubmit={this.handleSubmit}>
          {questions.map((question, index) => (
            <div className="question-container" key={index}>
              <p className="question-text">{question}</p>
              <div className="radio-options">
                <FormControl component="fieldset">
                  <RadioGroup
                    value={choices[index]}
                    onChange={(e) => this.handleChange(index, e.target.value)}
                  >
                    <FormControlLabel
                      value="Excellent"
                      control={<Radio />}
                      label="Excellent"
                    />
                    <FormControlLabel
                      value="Good"
                      control={<Radio />}
                      label="Good"
                    />
                    <FormControlLabel
                      value="Fair"
                      control={<Radio />}
                      label="Fair"
                    />
                    <FormControlLabel
                      value="Poor"
                      control={<Radio />}
                      label="Poor"
                    />
                  </RadioGroup>
                </FormControl>
              </div>
            </div>
          ))}
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </form>
      </div>
    );
  }
}

export default FeedbackForm;
