import React, { Component } from "react";
import {
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
} from "@material-ui/core";
import "./FeedbackForm.css";

class FeedbackForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      choices: [],
      dataFetched: false, // Track if data has been fetched
    };
  }

  componentDidUpdate() {
    // Check if data has already been fetched
    if (!this.state.dataFetched) {
      fetch(
        "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
      )
        .then((response) => response.json())
        .then((data) => {
          if (data && data.feedbackQuestions && data.choices) {
            // Process data and set state
            this.setState({
              questions: data.feedbackQuestions,
              choices: data.choices, // Set choices directly from API response
              dataFetched: true, // Update dataFetched state
            });
          } else {
            console.error(
              "Error fetching data: Questions or choices data not found"
            );
          }
        })
        .catch((error) => console.error("Error fetching data:", error));
    }
  }

  handleChange = (index, choice) => {
    this.setState((prevState) => {
      const updatedChoices = [...prevState.choices];
      updatedChoices[index] = choice;
      return { choices: updatedChoices };
    });
  };

  handleSubmit = (e) => {
    e.preventDefault(); // Prevent form submission

    const { questions, choices } = this.state;

    // Check if any question has not been answered
    if (choices.length !== questions.length || choices.includes("")) {
      alert("Please answer all questions before submitting.");
      return; // Exit early if any question is not answered
    } else {
      alert("Form submitted successfully");
    }

    // Construct feedback object
    const feedback = { feedback: { questions, choices } };

    // Perform submission logic
    console.log(feedback); // You can replace this with actual submission logic
  };

  render() {
    const { questions, choices } = this.state;

    return (
      <div className="container">
        <h1>Feedback form</h1>
        <form onSubmit={this.handleSubmit}>
          {questions.map((question, index) => (
            <div className="question-container" key={index}>
              <p className="question-text">{question}</p>
              <div className="radio-options">
                <FormControl component="fieldset">
                  <RadioGroup
                    value={choices[index]}
                    onChange={(e) => this.handleChange(index, e.target.value)}
                    className="radio-options"
                  >
                    {/* Check if choices[index] is an array before mapping */}
                    {Array.isArray(choices[index]) &&
                      choices[index].map((choice, choiceIndex) => (
                        <FormControlLabel
                          key={choiceIndex}
                          value={choice}
                          control={<Radio />}
                          checked={choices[index] === choice}
                          onChange={(e) => this.handleChange(index, choice)}
                          label={choice}
                        />
                      ))}
                  </RadioGroup>
                </FormControl>
              </div>
            </div>
          ))}
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </form>
      </div>
    );
  }
}

export default FeedbackForm;
