import React, { Component } from "react";
import {
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
} from "@material-ui/core";

class FeedbackForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      choices: [],
      submittedFeedback: null,
    };
  }

  componentDidMount() {
    // Fetch data from API
    fetch(
      "https://brijfeedback.pythonanywhere.com/api/get-feedback-questions/?unitID=1"
    )
      .then((response) => response.json())
      .then((data) => {
        // Update state with questions and choices
        this.setState({
          questions: data.questions,
          choices: Array(data.questions.length).fill(""),
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }

  handleChoiceChange = (index, choice) => {
    const choices = [...this.state.choices];
    choices[index] = choice;
    this.setState({ choices });
  };

  handleSubmit = () => {
    const { questions, choices } = this.state;
    const feedback = {
      questions,
      choices,
    };
    this.setState({ submittedFeedback: feedback });
  };

  render() {
    const { questions, choices, submittedFeedback } = this.state;

    return (
      <div>
        <form>
          {questions.map((question, index) => (
            <div key={index}>
              <FormControl component="fieldset">
                <RadioGroup
                  aria-label={question}
                  name={question}
                  value={choices[index]}
                  onChange={(e) =>
                    this.handleChoiceChange(index, e.target.value)
                  }
                >
                  {question.choices.map((choice, i) => (
                    <FormControlLabel
                      key={i}
                      value={choice}
                      control={<Radio />}
                      label={choice}
                    />
                  ))}
                </RadioGroup>
              </FormControl>
            </div>
          ))}
          <Button
            variant="contained"
            color="primary"
            onClick={this.handleSubmit}
          >
            Submit
          </Button>
        </form>

        {submittedFeedback && (
          <div>
            <h2>Submitted Feedback</h2>
            <pre>{JSON.stringify(submittedFeedback, null, 2)}</pre>
          </div>
        )}
      </div>
    );
  }
}

export default FeedbackForm;
