import React, { Component } from "react";
import {
  Radio,
  FormControlLabel,
  FormControl,
  FormLabel,
  Button,
  Typography,
  Container,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  RadioGroup,
} from "@material-ui/core";

class FeedbackForm extends Component {
  state = {
    questions: [],
    companyLogo: "",
    unitName: "",
    showDialog: false,
  };

  componentDidMount() {
    this.fetchQuestions();
  }

  fetchQuestions = () => {
    // Dummy fetch function. Replace URL with your actual data source
    fetch("https://yourapi.com/api/get-feedback-questions/?unitID=1")
      .then((response) => response.json())
      .then((data) => {
        const questions = data.feedbackQuestions.map((question) => ({
          ...question,
          answer: "",
        }));
        this.setState({
          questions,
          companyLogo: data.companyLogo,
          unitName: data.unitName,
        });
      })
      .catch((error) => console.error("Error fetching questions:", error));
  };

  handleChoiceChange = (event, index) => {
    const newQuestions = this.state.questions.slice();
    newQuestions[index].answer = event.target.value;
    this.setState({ questions: newQuestions });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Process submission here
    console.log("Submitted feedback:", this.state.questions);
    this.setState({ showDialog: true });
  };

  handleCloseDialog = () => {
    this.setState({ showDialog: false }, () => {
      window.location.reload();
    });
  };

  render() {
    return (
      <Container>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <img
              src={this.state.companyLogo}
              alt="Company Logo"
              style={{ width: "150px", marginBottom: "20px" }}
            />
            <Typography variant="h4">
              Feedback for {this.state.unitName}
            </Typography>
          </Grid>
        </Grid>

        <form onSubmit={this.handleSubmit}>
          <Table>
            <TableBody>
              {this.state.questions.map((question, index) => (
                <TableRow key={index}>
                  <TableCell>
                    <FormControl component="fieldset">
                      <FormLabel component="legend">
                        {question.question}
                      </FormLabel>
                      <RadioGroup
                        aria-label={question.question}
                        name={`question${index}`}
                        value={question.answer}
                        onChange={(event) =>
                          this.handleChoiceChange(event, index)
                        }
                      >
                        {question.choices.map((choice, choiceIndex) => (
                          <FormControlLabel
                            key={choiceIndex}
                            value={choice}
                            control={<Radio />}
                            label={choice}
                          />
                        ))}
                      </RadioGroup>
                    </FormControl>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
          <Button type="submit" variant="contained" color="primary">
            Submit Feedback
          </Button>
        </form>

        <Dialog open={this.state.showDialog} onClose={this.handleCloseDialog}>
          <DialogTitle>{"Feedback Submitted Successfully"}</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Thank you for your feedback. Here's a summary of your responses:
            </DialogContentText>
            <ul>
              {this.state.questions.map((question, index) => (
                <li
                  key={index}
                >{`${question.question}: ${question.answer}`}</li>
              ))}
            </ul>
          </DialogContent>
          <Button onClick={this.handleCloseDialog} color="primary">
            Close
          </Button>
        </Dialog>
      </Container>
    );
  }
}

export default FeedbackForm;
